  <h2 class="section-title">Book a Session or Workshop</h2>
  <p>
    Use the form below to request a workshop, masterclass, concert seat, private session, or studio time.
    We’ll follow up within 2 business days to confirm details.
  </p>

  <form id="bookingsForm" class="bookings-form">
    <input type="text" name="name" placeholder="Your Name" required />
    <input type="email" name="email" placeholder="Your Email" required />

    <select name="interest" required>
      <option value="">What would you like to book?</option>
      <option value="Workshop">Workshop</option>
      <option value="Masterclass">Masterclass</option>
      <option value="Concert">Concert</option>
      <option value="Private Session">Private Session</option>
      <option value="Studio Time">Studio Time</option>
      <option value="Residency">Residency</option>
    </select>

    <input type="text" name="dates" placeholder="Preferred Date(s) & Time(s)" required />

    <textarea name="details" placeholder="Additional Details (optional)"></textarea>

    <div class="g-recaptcha" data-sitekey="6Ld0NzYrAAAAAO9p29r0lAI__ztISnOaJ6UEocLH"></div>

    <button type="submit">Request Booking</button>
    <p id="booking-status" class="status-message"></p>
  </form>
</section>

<style>
  .bookings-section {
    max-width: 600px;
    margin: 3rem auto;
    padding: 1rem;
    color: var(--text-color);
  }

  .bookings-form input,
  .bookings-form select,
  .bookings-form textarea {
    width: 100%;
    margin: 0.5rem 0;
    padding: 0.75rem;
    border: 1px solid #444;
    background: #1e1e1e;
    color: #fff;
    border-radius: 5px;
  }

  .bookings-form button {
    background-color: var(--accent-color);
    color: white;
    padding: 0.75rem 1.5rem;
    border: none;
    border-radius: 6px;
    cursor: pointer;
  }

  .status-message {
    margin-top: 1rem;
    font-size: 0.9rem;
  }
</style>

<script src="https://www.google.com/recaptcha/api.js" async defer></script>


<script>
document.getElementById("bookingsForm").addEventListener("submit", async (e) => {
  e.preventDefault();
  const form = e.target;

  // Optional: show a loading message
  const status = document.getElementById("booking-status");
  if (status) {
    status.textContent = "Sending your request...";
  }


    // 🔒 Temporarily disable CAPTCHA check
  // const captchaResponse = grecaptcha.getResponse();
  // if (!captchaResponse) {
  //   status.textContent = "Please complete the CAPTCHA.";
  //   return;
  // }

  // Build JSON payload
  const payload = {
    name: form.name.value,
    email: form.email.value,
    interest: form.interest.value,
    preferred_dates: form.dates.value,
    details: form.details.value,
    source: "Bookings Page"
    // "g-recaptcha-response": grecaptcha.getResponse() // Uncomment if reCAPTCHA is re-enabled
  };

  try {
    const response = await fetch("https://script.google.com/macros/s/AKfycbzYfDxAmjGa_uMR8IHP_T7OlRhPIqMaUE82mZqoQM6RJskF9KDPEbJDnQdxFrnTXOnfGw/exec", {
      method: "POST",
      headers: {
        "Content-Type": "text/plain;charset=utf-8"
      },
      body: JSON.stringify(payload)
    });

    const html = await response.text();

    // ✅ Replace the entire bookings section with the server's HTML response
    document.querySelector(".bookings-section").innerHTML = html;

  } catch (err) {
    console.error(err);
    if (status) {
      status.textContent = "Failed to connect. Please try again later.";
    }
  }
});
</script>






