  <h2 class="section-title">Book a Session or Workshop</h2>
  <p>
    Use the form below to request a workshop, masterclass, concert seat, private session, or studio time.
    We’ll follow up within 2 business days to confirm details.
  </p>

  <form id="bookingsForm" class="bookings-form">
    <input type="text" name="name" id="name" placeholder="Your Name" required />
    <input type="email" name="email" id="email" placeholder="Your Email" required />

    <select name="interest" id="interest" required>
      <option value="">What would you like to book?</option>
      <option value="Workshop">Workshop</option>
      <option value="Masterclass">Masterclass</option>
      <option value="Concert">Concert</option>
      <option value="Private Session">Private Session</option>
      <option value="Studio Time">Studio Time</option>
      <option value="Residency">Residency</option>
    </select>

    <input type="text" name="dates" id="dates" placeholder="Preferred Date(s) & Time(s)" required />

    <textarea name="details" id="details" placeholder="Additional Details (optional)"></textarea>

    <div class="g-recaptcha" data-sitekey="6Ld0NzYrAAAAAO9p29r0lAI__ztISnOaJ6UEocLH"></div>

    <button type="submit">Request Booking</button>
    <p id="booking-status" class="status-message"></p>
  </form>
</section>

<style>
  .bookings-section {
    max-width: 600px;
    margin: 3rem auto;
    padding: 1rem;
    color: var(--text-color);
  }

  .bookings-form input,
  .bookings-form select,
  .bookings-form textarea {
    width: 100%;
    margin: 0.5rem 0;
    padding: 0.75rem;
    border: 1px solid #444;
    background: #1e1e1e;
    color: #fff;
    border-radius: 5px;
  }

  .bookings-form button {
    background-color: var(--accent-color);
    color: white;
    padding: 0.75rem 1.5rem;
    border: none;
    border-radius: 6px;
    cursor: pointer;
  }

  .status-message {
    margin-top: 1rem;
    font-size: 0.9rem;
  }
</style>

<script src="https://www.google.com/recaptcha/api.js" async defer></script>

<script>
document.addEventListener('DOMContentLoaded', function() {
  const form = document.getElementById('bookingsForm');
  const status = document.getElementById('booking-status');

  form.addEventListener('submit', async function(e) {
    e.preventDefault();
    status.textContent = '';

    // Uncomment to enable CAPTCHA check
     const captchaResponse = grecaptcha.getResponse();
     if (!captchaResponse) {
       status.textContent = "Please complete the CAPTCHA.";
       return;
    }

    const formData = {
      spreadsheetId: "1E45f3vjLTwZ8SaL0DnzZDOaqO0OKwVDtQ-KySkM2fUs",
      sheetName: "contact1",
      name: document.getElementById('name').value,
      email: document.getElementById('email').value,
      message: document.getElementById('details').value,
      interest: document.getElementById('interest').value,
      dates: document.getElementById('dates').value,
      details: document.getElementById('details').value,
      source: "booking",
      "g-recaptcha-response": captchaResponse 
    };

    const proxyUrl = "http://localhost:8180/proxy-api/forms";
    const scriptUrl = "https://script.google.com/macros/s/AKfycbzeVDklCszH6LCueLKPYtumw3kVjQPKKe3bcV2zsy5xbyqIv5w-sH7kbu-cz28H-_2p/exec";

    try {
      const response = await fetch(`${proxyUrl}?scriptUrl=${encodeURIComponent(scriptUrl)}`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData)
      });

      const result = await response.json();
      if (result.success) {
        status.textContent = "✅ " + result.message;
        form.reset();
         grecaptcha.reset(); // Uncomment if using CAPTCHA
      } else {
        status.textContent = "❌ " + (result.error || "Unknown error");
      }
    } catch (err) {
      status.textContent = "❌ Network error: " + err.message;
    }
  });
});
</script>




